tinymce.PluginManager.add('jbimages', function(editor, url) { function jbBox() { editor.windowManager.open({ title: 'Upload Image', file: url+'/dialog-v4.htm', width: 350,	height: 135, buttons: [{ text: 'Close', onclick: 'close' }] }); } editor.addButton('jbimages', { tooltip: 'Upload Image', icon: 'image', onclick: jbBox }); editor.addMenuItem('jbimages', { text: 'Upload Image', icon: 'image', context: 'insert', onclick: jbBox }); });